  return count ? (
    <div>
      <Link href={{ pathname: '/', query: { tag: children } }}>
        <a className={cn(styles.tag, className)} {...props}>
          {children}
        </a>
      </Link>
      <span className={styles.multiplier}>Ã—</span>
      &nbsp;
      <span className={styles.count}>{count}</span>
    </div>
  ) : (
    <Link href={{ pathname: '/', query: { tag: children } }}>
      <a className={cn(styles.tag, className)} {...props}>
        {children}
      </a>
    </Link>
  )
}

export default Tag
